import React, { Component } from 'react';
import Loading from './Loading';
import PersonCard from './PersonCard';

export default class App extends Component {
  constructor() {
    super();
    this.state = { loading: true, person: [] };
  }

  componentDidMount() {
    const endpoint = 'https://api.randomuser.me/';
    fetch(endpoint)
      .then((response) => response.json())
      .then((data) => {
        this.setState({ person: data.results, loading: false });
      })
  }

  shouldComponentUpdate(_nextProps, nextState) {
    const AGE = 50;

    if (nextState.person[0].dob.age > AGE) {
      return false;
    }
    return true;
  }

  getUserElements(user) {
    return {
      name: `${user.name.first} ${user.name.last}`,
      email: user.email,
      age: user.dob.age,
      image: user.picture.thumbnail,
    };
  }

  render() {
    const { person, loading } = this.props

    if (loading) return <Loading />;

    return (
      <div>
        {person.map((currentPerson) => (
          <PersonCard
            key={ currentPerson.login.uuid }
            person={ this.getUserElements(currentPerson) }
          />))}
      </div>
    )
  }
}
